#!/bin/bash\n\n# Jewelry Shop Management System - Setup Script\n# This script sets up the development environment\n\necho \"Setting up Jewelry Shop Management System...\"\n\n# Install root dependencies\necho \"Installing root dependencies...\"\nnpm install\n\n# Install dependencies for all workspaces\necho \"Installing workspace dependencies...\"\nnpm run setup\n\n# Create necessary directories\nmkdir -p logs\nmkdir -p temp\nmkdir -p uploads\n\n# Set up database (if needed)\necho \"Database setup will be handled by Docker Compose\"\n\n# Create environment file from example\nif [ ! -f .env ]; then\n  echo \"Creating .env file...\"\n  cat > .env << EOF\n# Database Configuration\nDB_HOST=localhost\nDB_PORT=5432\nDB_NAME=jewelry_shop_db\nDB_USER=jeweler\nDB_PASSWORD=jeweler123\n\n# Redis Configuration\nREDIS_URL=redis://localhost:6379\n\n# JWT Configuration\nJWT_SECRET=your-super-secret-jwt-key-change-in-production\nJWT_EXPIRES_IN=24h\n\n# API Keys (replace with real values)\nGOLD_API_KEY=your-gold-api-key\nMETAL_PRICE_API_KEY=your-metal-price-api-key\nOPENAI_API_KEY=your-openai-api-key\nGEMINI_API_KEY=your-gemini-api-key\nAZURE_SPEECH_KEY=your-azure-speech-key\n\n# Payment Gateway Keys\nRAZORPAY_KEY_ID=your-razorpay-key-id\nRAZORPAY_KEY_SECRET=your-razorpay-secret\nSTRIPE_SECRET_KEY=your-stripe-secret-key\n\n# Notification Service Keys\nSENDGRID_API_KEY=your-sendgrid-key\nTWILIO_ACCOUNT_SID=your-twilio-sid\nTWILIO_AUTH_TOKEN=your-twilio-token\n\n# Environment\nNODE_ENV=development\nLOG_LEVEL=info\nFRONTEND_URL=http://localhost:3000\nEOF\nfi\n\necho \"Setup complete!\"\necho \"Next steps:\"\necho \"1. Update .env file with your API keys\"\necho \"2. Run 'npm run docker:dev' to start all services\"\necho \"3. Access the application at http://localhost:3000\"\n